import React, { Component } from 'react';
import MaterialTable, {MTableHeader}from 'material-table';

import MTableToolbar from 'material-table';
import Groupbar from 'material-table';



import Input from '@material-ui/core/Input';
import "../App.css"

import Papa from 'papaparse';


import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';


// default param in const?
class Table extends Component {

constructor(props) {
  super(props);
  // Default
  this.state = {
    columns: [],
    data: [],
    isLoading: false
  };
}

search = () =>{


}

uploadCsvFile = (event) => {
  Papa.parse(event.target.files[0], {
    header: true,
    skipEmptyLines: true,
    dynamicTyping: true,
    complete: this.updateTable
  });
}


updateTable = (result) => {

  var NewColumns = [];
  var NewData = [];

  result.meta.fields.forEach(function(field) {
    NewColumns.push({
      title: field.toUpperCase(),
      field: field.toLowerCase(),
      cellStyle: {padding:1,color:'red', textAlign:'center'}

    });
  });

  result.data.forEach(function(data) {
    let newObject = {};
    for (var key in data) {
      if (data.hasOwnProperty(key)) {
        newObject[key.toLowerCase()] = data[key]
      }
    }
    NewData.push(newObject);
  });

  this.setState({
    columns: NewColumns,
    data: NewData
  });

  this.saveAllToDb();
}


saveAllToDb = () => {

  //this.setState({isLoading : true})
  console.log(this.state.isLoading);

  fetch(new Request('/db/addAllData', {
      method: 'POST',
      redirect: 'follow',
      headers: new Headers({
        'Content-Type': 'application/json'
      })
    }), {
      credentials: 'include',
      body: JSON.stringify(this.state.data)
    })
    .then(response => console.log)
    .catch(error => console.error)

  console.log(this.state.isLoading);
  //this.setState({isLoading:false})

}


addOrUpdateToDb = (data) => {

  fetch(new Request('/db/addOrUpdateData', {
      method: 'POST',
      redirect: 'follow',
      headers: new Headers({
        'Content-Type': 'application/json'
      })
    }), {
      credentials: 'include',
      body: JSON.stringify(data)
    })
    .then(response => console.log)
    .catch(error => console.error)

}


deleteFromDb = (data) => {

  fetch(new Request('/db/deleteData', {
      method: 'POST',
      redirect: 'follow',
      headers: new Headers({
        'Content-Type': 'application/json'
      })
    }), {
      credentials: 'include',
      body: JSON.stringify(data)
    })
    .then(response => console.log)
    .catch(error => console.error)

}


setData = () => {
  console.log("SETDATA From TABLE");

  var NewData = [];
  var column = "name";

  this.state.data.forEach(function(data) {
    NewData.push(
      data[column]
    );
  });
  NewData.sort(this.compare);
  this.props.setData({
    column: column.toUpperCase(),
    data: NewData
  });
}

compare(a, b) {
  if (typeof(a) == "number")  {
    return a - b;
  } else {
    if (a > b) {
           return 1;
       }
       if (b > a) {
           return -1;
       }
       return 0;
  }
}


  render = () => {
      return (
        <div id = "">
        <h2>Ezops - DB chart generator</h2>
          <div id = "fileUploader">
          <input
            accept=".csv"
            type="file"
            onChange = {this.uploadCsvFile}
          />
          </div>
          <h3>Database Table</h3>


          <MaterialTable
             options={{
                showTitle: false,
                toolbarButtonAlignment: "left",
                  headerStyle: {color:'green', textAlign:"center"},

              }}

            isLoading={this.state.isLoading}
            columns={this.state.columns}
            data={this.state.data}
            search= {true}

           editable={{
             onRowAdd: newData =>
               new Promise((resolve, reject) => {
                 setTimeout(() => {
                   {
                     const data = this.state.data;
                     data.push(newData);
                     console.log(newData);
                     this.addOrUpdateToDb(newData);

                     this.setState({ data }, () => resolve());

                   }
                   resolve()
                 }, 1000)
               }),
             onRowUpdate: (newData, oldData) =>
               new Promise((resolve, reject) => {
                 setTimeout(() => {
                   {
                     const data = this.state.data;
                     const index = data.indexOf(oldData);
                     data[index] = newData;
                     this.addOrUpdateToDb(newData);
                     this.setState({ data }, () => resolve());
                   }
                   resolve()
                 }, 1000)
               }),
             onRowDelete: oldData =>
               new Promise((resolve, reject) => {
                 setTimeout(() => {
                   {
                     let data = this.state.data;
                     const index = data.indexOf(oldData);
                     data.splice(index, 1);
                     this.deleteFromDb(oldData);

                     this.setState({ data }, () => resolve());
                   }
                   resolve()
                 }, 1000)
               }),
             }}

           />
           <Button variant="contained" color="primary" id = "uploadBtn" onClick = {this.setData}>
        Update Charts
       </Button>
      </div>
      );
      }
}
export default Table;
