import React, {Component} from 'react';
import Highcharts from 'highcharts'
import HighchartsReact from 'highcharts-react-official'

// Load Highcharts modules
require('highcharts/modules/histogram-bellcurve')(Highcharts);

const bar_options = {
  chart: {
    type: 'column'
  },
  title: {
    text: 'Monthly Average Rainfall'
  },
  subtitle: {
    text: 'Source: WorldClimate.com'
  },
  xAxis: {
    categories: [
      1,
      2,
    ],
    crosshair: true
  },
  yAxis: {
    min: 0,
    tickInterval: 1,
    title: {
      text: 'Rainfall (mm)'
    }
  },
  tooltip: {
    headerFormat: '<span style="font-size:10px">{point.key}</span><table>',
    pointFormat: '<tr><td style="color:{series.color};padding:0">{series.name}: </td>' +
      '<td style="padding:0"><b>{point.y:.1f} mm</b></td></tr>',
    footerFormat: '</table>',
    shared: true,
    useHTML: true
  },
  plotOptions: {
    column: {
      pointPadding: 0,
      borderWidth: 1,
      groupPadding: 0.2,
      shadow: false
    }
  },
  series: [{
    name: 'Data',
    data: [42, 55]

  }]
}

const pie_options = {
  chart: {
    plotBackgroundColor: null,
    plotBorderWidth: null,
    plotShadow: false,
    type: 'pie'
  },
  title: {
    text: 'Browser market shares in January, 2018'
  },
  tooltip: {
    pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>'
  },
  plotOptions: {
    pie: {
      allowPointSelect: true,
      cursor: 'pointer',
      dataLabels: {
        enabled: true,
        format: '<b>{point.name}</b>: {point.percentage:.1f} %'
      }
    }
  },
  series: [{
    name: 'Brands',
    colorByPoint: true,
    data: [{
      name: '0',
      y: 65,
      sliced: true,
      selected: true
    }, {
      name: '1',
      y: 11.84
    }]
  }]
}



class Chart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      barChartSettings: bar_options,
      pieChartSettings: pie_options
    };
  }

  countFrequency = (arr) => {
    var counts = {};
    for (var i = 0; i < arr.length; i++) {
      var num = arr[i];
      counts[num] = counts[num] ? counts[num] + 1 : 1;
    }
    return counts;
  }

  parseData = (arr) => {

    var counts = this.calculateFrequency(arr);
    var columns = [];
    var barChartData = [];
    var pieChartData = [];

    columns = Object.keys(counts);
    barChartData = Object.values(counts);

    Object.keys(counts).forEach(function(key) {
      let dataObject = {};
      dataObject = {
        name: key,
        y: counts[key]
      }
      pieChartData.push(dataObject);
    });

    return {
      newColumns: columns,
      newBarChartData: barChartData,
      newPieChartData: pieChartData
    };

  }

  componentDidMount() {

  }

  componentDidUpdate(prevProps, prevState, snapshot) {

    if (prevProps !== this.props) {
      const {
        newColumns,
        newBarChartData,
        newPieChartData
      } = this.parseData(this.props.data);

      this.setState({
        barChartSettings: {
          xAxis: {
            categories: newColumns
          },
          series: [{
            data: newBarChartData
          }]
        },

        pieChartSettings: {
          series: [{
            data: newPieChartData
          }]
        }
      });

    }

  }





  setChartData = (newCategories, newChartData_bar, newChartData_pie) => {

    this.setState({
      barChartSettings: {
        xAxis: {
          categories: newCategories
        },
        series: newChartData_bar
      },

      pieChartSettings: {
        series: {
          data: newChartData_pie
        }
      }
    });

  }

  render(){
    return(
      <div>
      <h3>Charts</h3>


      <HighchartsReact
        highcharts={Highcharts}
        options = {this.state.barChartSettings}
      />
      <HighchartsReact
        highcharts={Highcharts}
        options = {this.state.pieChartSettings}
      />

      </div>

    );

}
}
export default Chart;
