// Constants for styles in the app

import ErrorIcon from '@material-ui/icons/Error';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';

// App.js
export const APP = {
  textAlign: 'center'
};

// Chart.js, Table.js
export const SECTION = {
  padding: '20px'
};

// Chart.js
export const CHART_CONTAINER = {
  border: '1px solid #cccccc',
  margin: '30px auto',
  padding: '20px'
};

// DbUpdateAlert.js
export const ICONS = [
  CheckCircleIcon, ErrorIcon
];
export const ICON = {
  marginRight: 10
};
export const SNACKBARCONTENT = [{
    backgroundColor: 'blue',
    display: 'flex',
    alignItems: 'center'
  },
  {
    backgroundColor: 'red',
    display: 'flex',
    alignItems: 'center'
  }
];
export const SNACKBARCONTENT_SPAN = {
  display: 'flex',
  alignItems: 'center'
};

// SelectCategoryModal.js
export const UPDATECHARTS_BTN = {
  margin: '50px'
};

// SearchBar.js
export const SEARCHBAR_WRAPPER = {
  display: 'inline-flex',
  margin: 15
};
export const SEARCH_TEXTFIELD = {
  marginRight: 20,
  marginBottom: 10
};
export const SEARCH_BTN = {
  alignSelf: 'center'
};

// Table.js
export const INPUT = {
  display: 'none'
};
export const TOOLBAR = {
  display: 'flex',
  padding: '10px 0'
};
export const TABLE = {
  padding: '0 8px'
};
export const SEARCHBAR_CONTAINER = {
  border: '5px',
  margin: 'auto'
};
