import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';

/*
const useStyles = makeStyles(theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {

    margin: theme.spacing(1),
    minWidth: 120,
  },
}));
*/
class Dialog2 extends Component{
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      value:"male"

    };
  }
  componentDidUpdate(prevProps) {
    console.log("UPDATEEE");
    if (prevProps !== this.props) {


      this.props.columns.map((column) =>(
        column.isChecked = false
        ));

      this.setState({columns:this.props.columns})
    }


}

handleChange = (event) =>{
 this.setState({value : event.target.value});
}

  handleClickOpen = () =>{
    this.setState({ open: true });

  }

  handleClose =  () =>{
    const temp = this.state.columns;
/*
      temp.map((column) =>(
      column.isChecked = false ))
*/
    this.setState({ columns : temp, open: false });
  }

  handleOk =  () =>{
    this.props.setCheckbox(this.state.value);
    this.setState({  open: false });
  }



  render = () => {
      return (
    <div>
      <Button variant="contained" color="primary"  onClick={this.handleClickOpen}>Update Charts</Button>
      <Dialog disableBackdropClick disableEscapeKeyDown open={this.state.open} onClose={this.handleClose}>
        <DialogTitle>Choose categories to generate charts.</DialogTitle>
        <DialogContent>
          <form>
          <FormControl component="fieldset">
            <FormLabel component="legend">Gender</FormLabel>
            <RadioGroup
              aria-label="Gender"
              name="gender1"
              value={this.state.value}
              onChange={this.handleChange}
            >
              <FormControlLabel value="female" control={<Radio />} label="Female" />

              { this.props.columns.map((column) =>(
                    <FormControlLabel value={column.field} control={<Radio />} label={column.title} />
              ))
              }
            </RadioGroup>
          </FormControl>
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={this.handleOk} color="primary">
            Ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
  }
}


export default Dialog2;
