import React, { Component } from 'react';

import Button from '@material-ui/core/Button';
import Snackbar from '@material-ui/core/Snackbar';
import CloseIcon from '@material-ui/icons/Close';
import IconButton from '@material-ui/core/IconButton';
import { withStyles } from '@material-ui/styles';

import * as messages from '../chartSettings/messages';
import ErrorIcon from '@material-ui/icons/Error';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import SnackbarContent from '@material-ui/core/SnackbarContent';

const SUCCESS_TABLE_UPDATE = messages.SUCCESS_TABLE_UPDATE;
const SUCCESS_DB_ADD = messages.SUCCESS_DB_ADD;
const SUCCESS_DB_UPDATE = messages.SUCCESS_DB_UPDATE;
const SUCCESS_DB_DELETE = messages.SUCCESS_DB_DELETE;

const ERROR_DB_ADD= messages.ERROR_DB_ADD;
const ERROR_DB_UPDATE_DELETE = messages.ERROR_DB_UPDATE_DELETE;



const variantIcon = [
  CheckCircleIcon, ErrorIcon
];

const style = {
  display: 'flex',
  alignItems: 'center'
}

const style2 = {
  marginRight: 10,
}

const style3 = {
  backgroundColor: 'green'
}

class Alert extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      vertical: 'top',
      horizontal: 'center',
      message: 'hhh'
    };
  }

  componentDidUpdate(prevProps) {
    if (prevProps !== this.props) {
      var newMessage = '';

    switch (this.props.flag) {
    case 0:
      newMessage = SUCCESS_TABLE_UPDATE;
      break;
    case 1:
      newMessage = SUCCESS_DB_ADD;
      break;
    case 2:
      newMessage = ERROR_DB_ADD;
      break;
    case 3:
        newMessage =  SUCCESS_DB_UPDATE;
      break;
    case 4:
  newMessage = ERROR_DB_UPDATE_DELETE;
      break;
    case 5:
newMessage = SUCCESS_DB_DELETE;

    }
      this.setState({ message: newMessage, open: true});
    }
  }

  handleClick = newState => () => {
    this.setState({ open: true });
  }

  handleClose = () => {
    this.setState({
      open: false
    });
  }

  renderIcon(){
      const Icon = variantIcon[0];
      return(
          <SnackbarContent
            style = {style3}
            aria-describedby="client-snackbar"
            message={
              <span id="client-snackbar" style = {style}>
                <Icon style = {style2}/>
                {this.state.message}
              </span>
            }
            action={[
              <IconButton key="close" aria-label="close" color="inherit" onClick={this.handleClose}>
                <CloseIcon/>
              </IconButton>,
            ]}
          />
      );
    }

  render = () => {
      const { classes } = this.props;
      return (
        <div>
          <Button onClick={this.handleClick({ vertical: 'top', horizontal: 'center' })}>Top-Center</Button>
          <Snackbar
            anchorOrigin={{vertical: this.state.vertical, horizontal: this.state.horizontal }}
            open={this.state.open}
            autoHideDuration={3000}
            onClose={this.handleClose}
          >
          {this.renderIcon()}
          </Snackbar>
        </div>
      );
  }
}

export default Alert;
