  updateTable = (result) => {
    console.log(result);

    console.log(result.meta.fields)

  }



  render = () => {
    return (
      //<div className="App">
  <React.Fragment>

        <SearchBar/>

      <Table setData = {this.setData}/>
      <Chart columns = {this.state.columns} data = {this.state.data}/>


      </React.Fragment>
    )
  }
}

export default App;


