import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';

class UpdateChartsDialog extends Component{
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      value:"male"

    };
  }
  componentDidUpdate(prevProps) {
    console.log("UPDATEEE");
    if (prevProps !== this.props) {

      this.setState({columns:this.props.columns})
    }


}

handleChange = (event) =>{
 this.setState({value : event.target.value});
}

  handleClickOpen = () =>{
    this.setState({ open: true });

  }

  handleClose =  () =>{
    const temp = this.state.columns;
    this.setState({ columns : temp, open: false });
  }

  handleOk =  () =>{
    this.props.setData(this.state.value);
    this.setState({  open: false });
  }

  render = () => {
      return (
    <div>
      <Button variant="contained" color="primary" onClick={this.handleClickOpen} id ="update charts">Update Charts</Button>
      <Dialog disableBackdropClick disableEscapeKeyDown open={this.state.open} onClose={this.handleClose}>
        <DialogTitle>Choose a category to generate charts.</DialogTitle>
        <DialogContent>
          <form>
          <FormControl component="fieldset">
            <FormLabel component="legend">Category</FormLabel>
            <RadioGroup
              aria-label="Gender"
              name="gender1"
              value={this.state.value}
              onChange={this.handleChange}
            >
              { this.props.columns.map((column) =>(
                    <FormControlLabel value={column.field} control={<Radio />} label={column.title} />
              ))
              }
            </RadioGroup>
          </FormControl>
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={this.handleOk} color="primary">
            Ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
  }
}


export default UpdateChartsDialog;
