import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import AccountCircle from '@material-ui/icons/AccountCircle';
import InputAdornment from '@material-ui/core/InputAdornment';

/*
Represent search bar in the database.
*/
class SearchBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value : ''
    };
  }

  handleChange = (event) => {
    this.setState({
      value: event.target.value
    });
  }

  search = (event) => {
      fetch(`/db/search/?keyword=${this.state.value}`, {
        credentials: 'include',
        method: 'GET'
      })
      .then(function(response) {
        if(!response.ok){
          return false;
        }
        //console.log(response.json());
        return response.json();
      })
      .then((data) => {
          console.log(data);
          this.props.setSearchResult(data);
          return data;
      })
      .catch(function(error) {
        window.alert('There has been a problem with your fetch operation: ' + error.message);
      });

  }

  render = () => {
      return (
        <div style={{ display: 'inline-flex', marginBottom: 20}}>
          <div>
            <TextField
              id="standard-name"
              label="keyword"
              onChange = {this.handleChange}
              margin="normal"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <AccountCircle onClick = {this.iconClick}/>
                  </InputAdornment>
                ),
              }}
            />
          </div>
          <div style={{ alignSelf: 'center' }}>
            <Button variant="outlined" onClick = {this.search}>
              Search
            </Button>
          </div>
        </div>
      );
  }

}

/*
