import IconButton from '@material-ui/core/IconButton';
import SnackbarContent from '@material-ui/core/SnackbarContent';

import * as messages from '../chartSettings/messages';
import ErrorIcon from '@material-ui/icons/Error';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
const S = messages.S;
const E = messages.E;

const useStyles1 = makeStyles(theme => ({
  success: {
    backgroundColor: green[600],
  },
  error: {
    backgroundColor: theme.palette.error.dark,
  },
  info: {
    backgroundColor: theme.palette.primary.main,
  },
  warning: {
    backgroundColor: amber[700],
  },
  icon: {
    fontSize: 20,
  },
  iconVariant: {
    opacity: 0.9,
    marginRight: theme.spacing(1),
  },
  message: {
    display: 'flex',
    alignItems: 'center',
  },
}));

const useStyles2 = makeStyles(theme => ({
  margin: {
    margin: theme.spacing(1),
  },
}));

const variantIcon = {
  success: CheckCircleIcon,
  error: ErrorIcon,
};

class Alert extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      vertical: 'top',
      horizontal: 'center',
      message: ''

    };
  }

  componentDidUpdate(prevProps) {
    if (prevProps !== this.props) {
      console.log(this.props.flag);
      var msg =S;
        if(this.props.flag ==1){

        msg = E;
        console.log(msg);
        console.log();
      }
this.setState({ message: msg, open: true});
      /*this.setState({message:E, open: true});*/

    }

  }


  handleClick = newState => () => {
    this.setState({ open: true });
  }

  handleClose = () => {
    this.setState({
      open: false
    });
  }


render_Icon = () => {
console.log("ICON RENDERNGG");

const Icon = variantIcon["error"];
return (
  <SnackbarContent

        aria-describedby="client-snackbar"
        message={
          <span id="client-snackbar">
            <Icon/>
            {this.state.message}
          </span>
        }
        action={[
          <IconButton key="close" aria-label="close" color="inherit" onClick={this.onClose}>
            <CloseIcon/>
          </IconButton>,
        ]}

      />
  );
}

    render = () => {
      return (
    <div>
      <Button onClick={this.handleClick({ vertical: 'top', horizontal: 'center' })}>Top-Center</Button>
      <Snackbar
        anchorOrigin={{vertical: this.state.vertical, horizontal: this.state.horizontal }}
        key={`${this.state.vertical},${this.state.horizontal}`}
        open={this.state.open}
        autoHideDuration={3000}
        onClose={this.handleClose}

      >
{this.render_Icon()}
      </Snackbar>
    </div>
  );
}
}

export default Alert;
