import React, { Component } from 'react';
import Highcharts from 'highcharts'
import HighchartsReact from 'highcharts-react-official'

require('highcharts/modules/histogram-bellcurve')(Highcharts);

const bar_options = {
  chart: {
    type: 'column'
  },
  title: {
    text: 'Titanic data'
  },
  subtitle: {
    text: 'Source: kaggle'
  },
  xAxis: {
    categories: [
      1,
      2,
    ],
    crosshair: true
  },
  yAxis: {
    min: 0,
    tickInterval: 1,
    title: {
      text: 'Count (person)'
    }
  },
  tooltip: {
    headerFormat: '<span style="font-size:10px">{point.key}</span><table>',
    pointFormat: '<tr><td style="color:{series.color};padding:0">{series.name}: </td>' +
      '<td style="padding:0"><b>{point.y}</b></td></tr>',
    footerFormat: '</table>',
    shared: true,
    useHTML: true
  },
  plotOptions: {
    column: {
      pointPadding: 0,
      borderWidth: 1,
      groupPadding: 0.2,
      shadow: false
    }
  },
  series: [{
    data: [42, 55]

  }]
}

const pie_options = {
  chart: {
    plotBackgroundColor: null,
    plotBorderWidth: null,
    plotShadow: false,
    type: 'pie'
  },
  title: {
    text: 'Titanic data'
  },
  subtitle: {
    text: 'Source: kaggle'
  },
  tooltip: {
    pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>'
  },
  plotOptions: {
    pie: {
      allowPointSelect: true,
      cursor: 'pointer',
      dataLabels: {
        enabled: true,
        format: '<b>{point.name}</b>: {point.percentage:.1f} %'
      }
    }
  },
  series: [{
    name: 'Brands',
    colorByPoint: true,
    data: [{
      name: '0',
      y: 65,
      sliced: true,
      selected: true
    }, {
      name: '1',
      y: 11.84
    }]
  }]
}

class Chart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      barChartSettings: bar_options,
      pieChartSettings: pie_options
    };
  }

  componentDidUpdate(prevProps) {
    if (prevProps !== this.props) {
      const {
        newColumns,
        newBarChartData,
        newPieChartData
      } = this.parseData(this.props.data);
      this.setChartData(newColumns, newBarChartData, newPieChartData);
    }
  }

  parseData = (arr) => {
    var counts = this.countFrequency(arr);
    var pieChartData = [];
    return {
      newColumns: Object.keys(counts),
      newBarChartData: Object.values(counts),
      newPieChartData: this.parse_pie_helper(counts)
    };
  }

  setChartData = (newColumns, newBarChartData, newPieChartData) => {
    this.setState({
      barChartSettings: {
        xAxis: {
          categories: newColumns
        },
        series: [{
          data: newBarChartData
        }]
      },

      pieChartSettings: {
        series: [{
          data: newPieChartData
        }]
      }
    });
  }

  countFrequency = (arr) => {
    var counts = {};
    for (var i = 0; i < arr.length; i++) {
      var data = arr[i];
      counts[data] = counts[data] ? counts[data] + 1 : 1;
    }
    return counts;
  }

  parse_pie_helper = (counts) => {
    var data = [];
    Object.keys(counts).forEach(function(key) {
      let dataObject = {};
      dataObject = {
        name: key,
        y: counts[key]
      }
      data.push(dataObject);
    });
    return data;
  }

  render(){
    return(
      <div>
        <h3>Charts</h3>
        <HighchartsReact
          highcharts={Highcharts}
          options = {this.state.barChartSettings}
        />
        <HighchartsReact
          highcharts={Highcharts}
          options = {this.state.pieChartSettings}
        />
      </div>
    );
  }
}

export default Chart;
