// Alert.js
import ErrorIcon from '@material-ui/icons/Error';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';

export const ICONS = [
  CheckCircleIcon, ErrorIcon
];

export const ICON = {
  marginRight: 10
};

export const SNACKBARCONTENT = [
  {backgroundColor: 'blue', display: 'flex', alignItems: 'center'},
  {backgroundColor: 'red' , display: 'flex', alignItems: 'center'}
];

export const SNACKBARCONTENT_SPAN = {
  display: 'flex',
  alignItems: 'center'
};

// Table.js
export const TABLE = { padding:'0 8px'};
export const SEARCHBARCONTAINER = {};

// SearchBar.js
export const SEARCHBARWRAPPER= {display: 'inline-flex', margin:15 };
export const SEARCHTEXTFIELD = {marginRight:20, marginBottom:10};
export const SEARCHBTN = {alignSelf: 'center'};


// Table.js, Chart.js
export const SECTION = {padding:20};
