import React, { Component } from 'react';

import Papa from 'papaparse';

import MaterialTable from 'material-table';
import { default as Dialog } from './UpdateChartsDialog';
import "../App.css"


class Table extends Component {

constructor(props) {
  super(props);
  this.state = {
    columns: [],
    data: [],
    isLoading: false,
  };
}

search = () =>{

}

uploadCsvFile = (event) => {
  Papa.parse(event.target.files[0], {
    header: true,
    skipEmptyLines: true,
    dynamicTyping: true,
    complete: this.updateTable
  });
}


updateTable = (result) => {

  var NewColumns = [];
  var NewData = [];

  result.meta.fields.forEach(function(field) {
    NewColumns.push({
      title: field.toUpperCase(),
      field: field.toLowerCase(),
      cellStyle: style
    });
  });

  result.data.forEach(function(data) {
    let newObject = {};
    for (var key in data) {
      if (data.hasOwnProperty(key)) {
        newObject[key.toLowerCase()] = data[key]
      }
    }
    NewData.push(newObject);
  });

  this.setState({
    columns: NewColumns,
    data: NewData
  });

  this.saveAllToDb();
}


saveAllToDb = () => {

  //this.setState({isLoading : true})
  console.log(this.state.isLoading);

  fetch(new Request('/db/addAllData', {
      method: 'POST',
      redirect: 'follow',
      headers: new Headers({
        'Content-Type': 'application/json'
      })
    }), {
      credentials: 'include',
      body: JSON.stringify(this.state.data)
    })
    .then(response => console.log)
    .catch(error => console.error)

  console.log(this.state.isLoading);
  //this.setState({isLoading:false})

}


addOrUpdateToDb = (data) => {

  fetch(new Request('/db/addOrUpdateData', {
      method: 'POST',
      redirect: 'follow',
      headers: new Headers({
        'Content-Type': 'application/json'
      })
    }), {
      credentials: 'include',
      body: JSON.stringify(data)
    })
    .then(response => console.log)
    .catch(error => console.error)

}


deleteFromDb = (data) => {

  fetch(new Request('/db/deleteData', {
      method: 'POST',
      redirect: 'follow',
      headers: new Headers({
        'Content-Type': 'application/json'
      })
    }), {
      credentials: 'include',
      body: JSON.stringify(data)
    })
    .then(response => console.log)
    .catch(error => console.error)

}


setData = (column) => {


  console.log("SETDATA From TABLE");
  console.log(column);

  var NewData = [];
  //var column = columns[0].title;

  this.state.data.forEach(function(data) {
    NewData.push(
      data[column]
    );
  });
  NewData.sort(this.compare);
  console.log(NewData);
  this.props.setData({
    column: column.toUpperCase(),
    data: NewData
  });


