compare(a, b) {
  if (typeof(a) == "number")  {
    return a - b;
  } else {
    if (a > b) {
           return 1;
       }
       if (b > a) {
           return -1;
       }
       return 0;
  }
}





  render = () => {
      return (
        <div id = "">
        <h2>DB chart generator</h2>
          <div id = "fileUploader">
          <input
            accept=".csv"
            type="file"
            onChange = {this.uploadCsvFile}
          />
          </div>
          <h3>Database Table</h3>

          <MaterialTable
             options={{
                showTitle: false,
                toolbarButtonAlignment: "left",
                headerStyle: style,
                addRowPosition:'first'
              }}
            isLoading={this.state.isLoading}
            columns={this.state.columns}
            data={this.state.data}
            search= {true}
           editable={{
             onRowAdd: newData =>
               new Promise((resolve, reject) => {
                 setTimeout(() => {
                   {
                     const data = this.state.data;
                     data.push(newData);
                     this.addOrUpdateToDb(newData);
                     this.setState({ data }, () => resolve());
                   }
                   resolve()
                 }, 1000)
               }),
             onRowUpdate: (newData, oldData) =>
               new Promise((resolve, reject) => {
                 setTimeout(() => {
                   {
                     const data = this.state.data;
                     const index = data.indexOf(oldData);
                     data[index] = newData;
                     this.addOrUpdateToDb(newData);
                     this.setState({ data }, () => resolve());
                   }
                   resolve()
                 }, 1000)
               }),
             onRowDelete: oldData =>
               new Promise((resolve, reject) => {
                 setTimeout(() => {
                   {
                     let data = this.state.data;
                     const index = data.indexOf(oldData);
                     data.splice(index, 1);
                     this.deleteFromDb(oldData);
                     this.setState({ data }, () => resolve());
                   }
                   resolve()
                 }, 1000)
               }),
             }}
           />

        <Dialog columns = {this.state.columns} setData = {this.setData}/>
      </div>
      );
      }
}
const style = {padding:'0px'}
export default Table;
