backgroundColor:'green'
}



class Alert extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      vertical: 'top',
      horizontal: 'center',
      message: 'hhh'
    };
  }

  componentDidUpdate(prevProps) {
    if (prevProps !== this.props) {
      console.log(this.props.flag);
      var msg =S;
        if(this.props.flag ==1){

        msg = E;
        console.log(msg);
        console.log();
      }
      this.setState({ message: msg, open: true});
      /*this.setState({message:E, open: true});*/

    }

  }


  handleClick = newState => () => {
    this.setState({ open: true });
  }

  handleClose = () => {
    this.setState({
      open: false
    });
  }

  renderIcon(){
      console.log("came here");
      const Icon = variantIcon[0];
const variant = 0;
      return(




          <SnackbarContent
            style = {style3}
            aria-describedby="client-snackbar"
            message={
              <span id="client-snackbar">
                <Icon/>
                {this.state.message}
              </span>
            }
            action={[
              <IconButton key="close" aria-label="close" color="inherit" onClick={this.handleClose}>
                <CloseIcon/>
              </IconButton>,
            ]}

          />

      );




    }


    render = () => {
 const { classes } = this.props;
      return (
    <div>
      <Button onClick={this.handleClick({ vertical: 'top', horizontal: 'center' })}>Top-Center</Button>

<Snackbar
  ContentProps={{
                      classes: {
                          root: classes.root
                      }
                  }}
        anchorOrigin={{vertical: this.state.vertical, horizontal: this.state.horizontal }}
        key={`${this.state.vertical},${this.state.horizontal}`}
        open={this.state.open}
        autoHideDuration={3000}
        onClose={this.handleClose}
        ContentProps={{
          'aria-describedby': 'message-id',
        }}
        message={
                <span id="client-snackbar" style = {style}>
                  {this.renderIcon()}
                  {this.state.message}
                </span>
              }

        action={[
          <IconButton
            key="close"
            aria-label="close"
            color="inherit"
            onClick={this.handleClose}
          >
            <CloseIcon />
          </IconButton>,
        ]}
      />
    </div>
  );
}
}

export default Alert;
