import React, { Component } from 'react';
import MaterialTable from 'material-table';


// default param in const?
class Table extends Component {

    constructor(props) {
      super(props);
      this.state = {
        columns : [
          { title: 'COL1', field: 'col1' , type: 'numeric'},
          { title: 'COL2', field: 'col2', type: 'numeric'},
          { title: 'COL3', field: 'col3', type: 'numeric' },
          { title: 'COL4', field: 'col4', type: 'numeric' },
        ],
        data: [
          { col1: 10, col2: 20, col3: 30, col4: 40 },
          {
            col1: 40,
            col2: 30,
            col3: 20,
            col4: 10
            ,
          },
        ]

      };



    }

    updateTable(){
      this.state = {
        columns : [
          { title: 'COL1', field: 'col1' , type: 'numeric'},
          { title: 'COL2', field: 'col2', type: 'numeric'},
          { title: 'COL3', field: 'col3', type: 'numeric' },
          { title: 'COL4', field: 'col4', type: 'numeric' },
        ],
        data: [
          { name: 'Daeun', surname: 'DAAA', birthYear: 1987, birthCity: 63 },
          {
            name: 'Dasom',
            surname: 'dddd',
            birthYear: 2017,
            birthCity: 34,
          },
        ]

      };
    }

    componentDidMount(){
    //  console.log("DID MOUNTTT");
      //console.log(this.state);
        this.props.setData(this.state);
    }

  render = () => {
  return (
    <MaterialTable
      title="Editable Example"
      columns={this.state.columns}
      data={this.state.data}
      editable={{
        onRowAdd: newData =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...this.state.data];
              data.push(newData);
              this.setState({ ...this.state, data });
              this.props.setData(this.state);
            }, 600);
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...this.state.data];
              data[data.indexOf(oldData)] = newData;
              this.setState({ ...this.state, data });
              this.props.setData(this.state);               //
            }, 600);
          }),
        onRowDelete: oldData =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...this.state.data];
              data.splice(data.indexOf(oldData), 1);
              this.setState({ ...this.state, data });
              this.props.setData(this.state);
            }, 600);
          }),
      }}
    />
  );
}
}
export default Table;
