import React, {Component} from 'react';
import Highcharts from 'highcharts'
import HighchartsReact from 'highcharts-react-official'

// Load Highcharts modules
require('highcharts/modules/histogram-bellcurve')(Highcharts);

const bar_options = {
  chart: {
    type: 'column'
  },
  title: {
    text: 'Monthly Average Rainfall'
  },
  subtitle: {
    text: 'Source: WorldClimate.com'
  },
  xAxis: {
    categories: [
      1,
      2,
    ],
    crosshair: true
  },
  yAxis: {
    min: 0,
    tickInterval: 1,
    title: {
      text: 'Rainfall (mm)'
    }
  },
  tooltip: {
    headerFormat: '<span style="font-size:10px">{point.key}</span><table>',
    pointFormat: '<tr><td style="color:{series.color};padding:0">{series.name}: </td>' +
      '<td style="padding:0"><b>{point.y:.1f} mm</b></td></tr>',
    footerFormat: '</table>',
    shared: true,
    useHTML: true
  },
  plotOptions: {
    column: {
      pointPadding: 0,
      borderWidth: 1,
      groupPadding: 0.2,
      shadow: false
    }
  },
  series: [{
    name: 'Data',
    data: [42, 55]

  }]
}

const pie_options = {
  chart: {
    plotBackgroundColor: null,
    plotBorderWidth: null,
    plotShadow: false,
    type: 'pie'
  },
  title: {
    text: 'Browser market shares in January, 2018'
  },
  tooltip: {
    pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>'
  },
  plotOptions: {
    pie: {
      allowPointSelect: true,
      cursor: 'pointer',
      dataLabels: {
        enabled: true,
        format: '<b>{point.name}</b>: {point.percentage:.1f} %'
      }
    }
  },
  series: [{
    name: 'Brands',
    colorByPoint: true,
    data: [{
      name: '0',
      y: 65,
      sliced: true,
      selected: true
    }, {
      name: '1',
      y: 11.84
    }]
  }]
}



class Chart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      barChartSettings: bar_options,
      pieChartSettings: pie_options
    };
  }

  calculate = (arr) => {

    var counts = {};

    var pie_data = [];
    var bar_cate = [];
    var bar_data = [];


    for (var i = 0; i < arr.length; i++) {
      var num = arr[i];
      counts[num] = counts[num] ? counts[num] + 1 : 1;
    }

    Object.keys(counts).forEach(function(key) {
      let newPieData = {};
      newPieData = {
        name: key,
        y: counts[key]
      }
      pie_data.push(newPieData);
    });

    bar_cate = Object.keys(counts);

    let newObject2 = {};
    newObject2 = {
      data: Object.values(counts)
    }

    bar_data = Object.values(counts);
    console.log(bar_data);

    //return counts;
    return {
      bar_cate: bar_cate,
      bar_data: bar_data,
      pie_data: pie_data
    };

  }

  componentDidMount() {

  }

  componentDidUpdate(prevProps, prevState, snapshot) {

    if (prevProps !== this.props) {
      const {
        bar_cate,
        bar_data,
        pie_data
      } = this.calculate(this.props.data);

      console.log(bar_cate);
      console.log(bar_cate.length);
      console.log(bar_data);
      console.log(pie_data);

      this.setState({
        barChartSettings: {
          xAxis: {
            categories: bar_cate
          },
          series: [{
            name: 'Data',
            data: bar_data

          }]
        },

        pieChartSettings: {
          series: [{
            data: pie_data
          }]
        }
      });

      /*
            var newCategories = this.props.columns.slice(1);

            var newChartData_bar = this.chartDataParser_bar(this.props.data);
            var newChartData_pie = this.chartDataParser_pie(newCategories, this.props.data);

            this.(newCategories, newChartData_bar, newChartData_pie);
      */
    }

    if (prevState !== this.state) {
      console.log("State diff");
      console.log(prevState);
      console.log(this.state);
    }

  }



  chartDataParser_bar = (dataList) => {

    var chartData = [];

    dataList.forEach(function(data) {
      var temp = Object.values(data);
      let newObject = {};
      newObject = {
        name: temp[0],
        data: temp.slice(1, -1).map(Number)
      }
      chartData.push(newObject);
    });

    return chartData;

  }


  chartDataParser_pie = (categories, dataList) => {

    var chartData = [];

    categories.forEach(function(category) {
      let newObject = {};
      var sum = 0;

      dataList.forEach(function(data) {
        sum += Number(data[category.toLowerCase()]);
      });

      newObject = {
        name: category,
        y: sum
      }
      chartData.push(newObject);
    });

    return chartData;

  }


  setChartData = (newCategories, newChartData_bar, newChartData_pie) => {

    this.setState({
      barChartSettings: {
        xAxis: {
          categories: newCategories
        },
        series: newChartData_bar
      },

      pieChartSettings: {
        series: {
          data: newChartData_pie
        }
      }
    });

  }

  render(){
    return(
      <div>
      <h3>Charts</h3>


      <HighchartsReact
        highcharts={Highcharts}
        options = {this.state.barChartSettings}
      />
      <HighchartsReact
        highcharts={Highcharts}
        options = {this.state.pieChartSettings}
      />

      </div>

    );

}
}
export default Chart;
