  }



  render = () => {
    return (
      //<div className="App">
  <React.Fragment>

        <SearchBar/>


        <Button
        variant="contained"
        component="label">
        Upload File
        <input
          type="file"
          accept=".csv"
          style={{ display: "none" }}
          onChange = {this.handleSubmit2}
        />
      </Button>
      <Table setData = {this.setData}/>
      <Chart columns = {this.state.columns} data = {this.state.data}/>


      </React.Fragment>
    )
  }
}

export default App;


