// Constant variables for alert styling - Alert.js

import ErrorIcon from '@material-ui/icons/Error';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';

export const ICONS = [
  CheckCircleIcon, ErrorIcon
];

export const ICON_STYLES = {
  marginRight: 10
};

export const SNACKBARCONTENT_STYlES = [
  {backgroundColor: 'blue', display: 'flex', alignItems: 'center'}, {backgroundColor: 'red' , display: 'flex', alignItems: 'center'}
];

export const SNACKBARCONTENT_STYlES_SPAN = {
  display: 'flex',
  alignItems: 'center'
};

export const TABLESTYLE = { padding:'0 8px'}
