import React, { Component } from 'react';

import Button from '@material-ui/core/Button';
import Snackbar from '@material-ui/core/Snackbar';
import CloseIcon from '@material-ui/icons/Close';
import IconButton from '@material-ui/core/IconButton';

class Alert extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      vertical: 'top',
      horizontal: 'center',
      message: 'hhhh'

    };
  }

  componentDidUpdate(prevProps) {
    if (prevProps !== this.props) {
      console.log(this.props.flag);
      this.setState({message:"lalalala"});
    }

  }


  handleClick = newState => () => {
    this.setState({ open: true });
  }

  handleClose = () => {
    this.setState({
      open: false
    });
  }


    render = () => {
      return (
    <div>
      <Button onClick={this.handleClick({ vertical: 'top', horizontal: 'center' })}>Top-Center</Button>
      <Snackbar
        anchorOrigin={{vertical: this.state.vertical, horizontal: this.state.horizontal }}
        key={`${this.state.vertical},${this.state.horizontal}`}
        open={this.state.open}
        autoHideDuration={3000}
        onClose={this.handleClose}
        ContentProps={{
          'aria-describedby': 'message-id',
        }}
        message={this.props.msg}
        action={[
          <IconButton
            key="close"
            aria-label="close"
            color="inherit"
            onClick={this.handleClose}
          >
            <CloseIcon />
          </IconButton>,
        ]}
      />
    </div>
  );
}
}

export default Alert;
